('email.templates.signup_verification', 'signup_verification Template', 'object', 'Email / Templates', 'Template for signup_verification', ARRAY['email', 'templates']::text[], '{"subject":"Verify your email","text":"Hello {{name}},\n\nPlease verify your email by opening this link:\n{{verification_link}}\n\nIf you did not create this account, ignore this email.","html":"<p>Hello {{name}},</p><p>Please verify your email by opening this link:</p><p><a href=\"{{verification_link}}\">Verify email</a></p><p>If you did not create this account, ignore this email.</p>"}'::jsonb, '{"subject":"Verify your email","text":"Hello {{name}},\n\nPlease verify your email by opening this link:\n{{verification_link}}\n\nIf you did not create this account, ignore this email.","html":"<p>Hello {{name}},</p><p>Please verify your email by opening this link:</p><p><a href=\"{{verification_link}}\">Verify email</a></p><p>If you did not create this account, ignore this email.</p>"}'::jsonb, false, now()),
('email.templates.verification_resend_confirmation', 'verification_resend_confirmation Template', 'object', 'Email / Templates', 'Template for verification_resend_confirmation', ARRAY['email', 'templates']::text[], '{"subject":"A new verification link has been sent","text":"Hello {{name}},\n\nA new verification link has been requested for this account.\n\nIf this was you, use the newest email in your inbox.","html":"<p>Hello {{name}},</p><p>A new verification link has been requested for this account.</p><p>If this was you, use the newest email in your inbox.</p>"}'::jsonb, '{"subject":"A new verification link has been sent","text":"Hello {{name}},\n\nA new verification link has been requested for this account.\n\nIf this was you, use the newest email in your inbox.","html":"<p>Hello {{name}},</p><p>A new verification link has been requested for this account.</p><p>If this was you, use the newest email in your inbox.</p>"}'::jsonb, false, now()),
